mapper.update:
UPDATE study_task
title = #{title}, description = #{description}, target_hours = #{targetHours}, status = #{status}, category = #{category}, deadline = #{deadline},
WHERE id = #{id}

mapper.select:
SELECT * FROM study_task
WHERE user_id = #{userId}

    AND title LIKE CONCAT('%', #{query.keyword}, '%')


    AND status = #{query.status}


    AND category = #{query.category}

ORDER BY create_time DESC
LIMIT #{query.pageSize} OFFSET ${(query.pageNum - 1) * query.pageSize}

SELECT COUNT(*) FROM study_task
WHERE user_id = #{userId}

    AND title LIKE CONCAT('%', #{query.keyword}, '%')


    AND status = #{query.status}


    AND category = #{query.category}

SELECT * FROM study_task
WHERE 1=1

    AND title LIKE CONCAT('%', #{query.keyword}, '%')


    AND status = #{query.status}


    AND category = #{query.category}

ORDER BY create_time DESC
LIMIT #{query.pageSize} OFFSET ${(query.pageNum - 1) * query.pageSize}

SELECT COUNT(*) FROM study_task
WHERE 1=1

    AND title LIKE CONCAT('%', #{query.keyword}, '%')


    AND status = #{query.status}


    AND category = #{query.category}
